mapper.select:
select 
 from zhxh_rfid_card
 where record_id in(
    select qty_card_id
    from zhxh_card_bind
    where workstation_bind_id in(
        select record_id from zhxh_workstation_bind
        where workstation_id = #{workstationId}
          and outsource_card_id = #{outsourceCardId}
        order by attach_time desc
        limit 1
    )
)

select count(*) as totalCount,
       sum(issue_qty) as totalQty
from zhxh_rfid_card rfid
where rfid.production_id = #{productionId}
  and rfid.card_status = 1;
